customFusionChartConfig:
{{(function () {
  let config = {
			type: "msline",
			dataSource: {
				chart: {
					caption: "Metric actions across PRs",
					subCaption: "Showing selected actions across PRs for the selected metric",
					xAxisName: "Lable created with <i>MM/DD (PR ID) - Meta ID</i>",
					yAxisName: "Number of " + utils.getUnitMapToMetric().unit,
					theme: "fusion"
    		},
				categories: [{category: []}],
				dataset: [],
				trendlines: [
        {
            line: []
        }
    ]
			}
		}

		let d =  Select2.selectedOptionValue == 'min' ? run_by_metric_min.data : run_by_metric.data

		// create the config object for the chart with the given data
		d.map((v) => {
				if(!_.find(config.dataSource.categories[0].category, {label: moment(v.created_at).format("MM/DD").toString() +" ("+ v.pull_request_id.toString() + ") - "+v.meta})) {
					config.dataSource.categories[0].category.push({"label": moment(v.created_at).format("MM/DD").toString() +" ("+ v.pull_request_id.toString() + ") - "+v.meta})
				}
				if(!_.find(config.dataSource.dataset, {seriesname: v.action})) {
					config.dataSource.dataset.push({seriesname: v.action, data: [{value: v.median}]})
				} else {
					_.find(config.dataSource.dataset, {seriesname: v.action}).data.push({value: v.median})
				}
		})

	if(config.dataSource.dataset.length > 0){
		config.dataSource.dataset.map((v) => {
			var x = _.meanBy(v.data, (p) => p.value)
			config.dataSource.trendlines[0].line.push({
				"startvalue": x,
				"color": Math.floor(Math.random()*16777215).toString(16),
				"valueOnRight": "1",
				"displayvalue": "Mean"
			})
		})
	}

		return config
})();}}
